import { useState } from 'react';
import './styles.css';

// ÊñπÂùóÁªÑ‰ª∂
function Square({ value, onSquareClick, isWinning }) {
  return (
    <button 
      className={`square ${isWinning ? 'winning-square' : ''}`}
      onClick={onSquareClick}
    >
      {value}
    </button>
  );
}

// Ê£ãÁõòÁªÑ‰ª∂ - ‰øÆÊ≠£Ë°åÂàóÈ°∫Â∫èÔºåÊúÄ‰∏äÈù¢ÊòØÁ¨¨‰∏ÄË°åÔºåÊúÄ‰∏ãÈù¢ÊòØÁ¨¨‰∏âË°å
function Board({ xIsNext, squares, onPlay, winningLine }) {
  function handleClick(i) {
    if (calculateWinner(squares) || isBoardFull(squares) || squares[i]) {
      return;
    }

    const nextSquares = squares.slice();
    nextSquares[i] = xIsNext ? 'X' : 'O'; // ‰∫§ÊõøÊòæÁ§∫XÂíåO
    onPlay(nextSquares);
  }

  function renderSquare(i) {
    const isWinningSquare = winningLine && winningLine.includes(i);
    return (
      <Square 
        key={i}
        value={squares[i]}
        onSquareClick={() => handleClick(i)}
        isWinning={isWinningSquare}
      />
    );
  }

  // ‰øÆÊ≠£ÔºöÊúÄ‰∏äÈù¢ÊòØÁ¨¨‰∏ÄË°å(Á¥¢Âºï0-2)ÔºåÊúÄ‰∏ãÈù¢ÊòØÁ¨¨‰∏âË°å(Á¥¢Âºï6-8)
  return (
    <>
      <div className="status">{status}</div>

      <div className="board">
        {/* Á¨¨‰∏ÄË°åÔºàÊúÄ‰∏äÈù¢‰∏ÄË°åÔºâ- Á¥¢Âºï0-2 */}
        <div className="board-row top-row">
          {renderSquare(0)} {/* Á¨¨1Ë°åÁ¨¨1Âàó */}
          {renderSquare(1)} {/* Á¨¨1Ë°åÁ¨¨2Âàó */}
          {renderSquare(2)} {/* Á¨¨1Ë°åÁ¨¨3Âàó */}
        </div>

        {/* Á¨¨‰∫åË°åÔºà‰∏≠Èó¥‰∏ÄË°åÔºâ- Á¥¢Âºï3-5 */}
        <div className="board-row middle-row">
          {renderSquare(3)} {/* Á¨¨2Ë°åÁ¨¨1Âàó */}
          {renderSquare(4)} {/* Á¨¨2Ë°åÁ¨¨2Âàó */}
          {renderSquare(5)} {/* Á¨¨2Ë°åÁ¨¨3Âàó */}
        </div>

        {/* Á¨¨‰∏âË°åÔºàÊúÄ‰∏ãÈù¢‰∏ÄË°åÔºâ- Á¥¢Âºï6-8 */}
        <div className="board-row bottom-row">
          {renderSquare(6)} {/* Á¨¨3Ë°åÁ¨¨1Âàó */}
          {renderSquare(7)} {/* Á¨¨3Ë°åÁ¨¨2Âàó */}
          {renderSquare(8)} {/* Á¨¨3Ë°åÁ¨¨3Âàó */}
        </div>
      </div>
    </>
  );

  // ËÆ°ÁÆóÊ∏∏ÊàèÁä∂ÊÄÅ
  const winner = calculateWinner(squares);
  const isBoardFilled = isBoardFull(squares);
  let status;

  if (winner) {
    status = 'Ëé∑ËÉúËÄÖ: ' + winner;
  } else if (isBoardFilled) {
    status = 'Âπ≥Â±ÄÔºÅ';
  } else {
    status = '‰∏ã‰∏Ä‰ΩçÁé©ÂÆ∂: ' + (xIsNext ? 'X' : 'O'); // Ê∏ÖÊô∞ÊòæÁ§∫‰∏ã‰∏Ä‰ΩçÁé©ÂÆ∂
  }
}

// ËæÖÂä©ÂáΩÊï∞ÔºöËÆ°ÁÆóËé∑ËÉúËÄÖ
function calculateWinner(squares) {
  const lines = [
    [0, 1, 2], // Á¨¨1Ë°åÔºàÊúÄ‰∏äÈù¢Ôºâ
    [3, 4, 5], // Á¨¨2Ë°åÔºà‰∏≠Èó¥Ôºâ
    [6, 7, 8], // Á¨¨3Ë°åÔºàÊúÄ‰∏ãÈù¢Ôºâ
    [0, 3, 6], // Á¨¨1ÂàóÔºàÊúÄÂ∑¶ËæπÔºâ
    [1, 4, 7], // Á¨¨2ÂàóÔºà‰∏≠Èó¥Ôºâ
    [2, 5, 8], // Á¨¨3ÂàóÔºàÊúÄÂè≥ËæπÔºâ
    [0, 4, 8], // ‰∏ªÂØπËßíÁ∫ø
    [2, 4, 6], // ÂâØÂØπËßíÁ∫ø
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

// ËæÖÂä©ÂáΩÊï∞ÔºöÊ£ÄÊµãÂπ≥Â±Ä
function isBoardFull(squares) {
  return squares.every(square => square !== null);
}

// Ê∏∏Êàè‰∏ªÁªÑ‰ª∂
export default function Game() {
  const [xIsNext, setXIsNext] = useState(true);
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);
  const [isAscending, setIsAscending] = useState(false); // ÈªòËÆ§ÈôçÂ∫èÔºåÁ¨¶ÂêàÂõæÁâáÊòæÁ§∫
  const [scores, setScores] = useState({ X: 0, O: 0, Âπ≥Â±Ä: 0 });

  const currentSquares = history[currentMove];
  const winner = calculateWinner(currentSquares);
  const isBoardFilled = isBoardFull(currentSquares);

  let status;
  if (winner) {
    status = 'Ëé∑ËÉúËÄÖ: ' + winner;
  } else if (isBoardFilled) {
    status = 'Âπ≥Â±ÄÔºÅ';
  } else {
    status = '‰∏ã‰∏Ä‰ΩçÁé©ÂÆ∂: ' + (xIsNext ? 'X' : 'O');
  }

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
    setXIsNext(!xIsNext);

    // Êõ¥Êñ∞ÂàÜÊï∞
    if (winner) {
      setScores(prev => ({ ...prev, [winner]: prev[winner] + 1 }));
    } else if (isBoardFilled) {
      setScores(prev => ({ ...prev, Âπ≥Â±Ä: prev.Âπ≥Â±Ä + 1 }));
    }
  }

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
    setXIsNext(nextMove % 2 === 0);
  }

  function generateMoveHistory() {
    const movesToRender = isAscending ? history : [...history].reverse();
    const moveIndices = isAscending 
      ? Array.from({length: history.length}, (_, i) => i) 
      : Array.from({length: history.length}, (_, i) => history.length - 1 - i);

    return movesToRender.map((squares, moveIndex) => {
      const actualMoveIndex = isAscending ? moveIndex : history.length - 1 - moveIndex;
      let description;

      if (actualMoveIndex === 0) {
        description = 'Ê∏∏ÊàèÂºÄÂßã';
      } else {
        // ‰øÆÊ≠£ÔºöÊúÄ‰∏äÈù¢ÊòØÁ¨¨1Ë°åÔºåÊúÄ‰∏ãÈù¢ÊòØÁ¨¨3Ë°å
        const row = Math.floor((actualMoveIndex - 1) / 3) + 1; // 1=Á¨¨1Ë°å(ÊúÄ‰∏ä), 3=Á¨¨3Ë°å(ÊúÄ‰∏ã)
        const col = ((actualMoveIndex - 1) % 3) + 1; // 1=Á¨¨1Âàó(ÊúÄÂ∑¶), 3=Á¨¨3Âàó(ÊúÄÂè≥)
        const player = actualMoveIndex % 2 === 1 ? 'X' : 'O'; // ‰øÆÊ≠£ÔºöÂ•áÊï∞Ê≠•ÊòØXÔºåÂÅ∂Êï∞Ê≠•ÊòØO
        description = `ÁßªÂä®#${actualMoveIndex}: ${player}‰∏ãÂú® (Á¨¨${row}Ë°å, Á¨¨${col}Âàó)`;
      }

      return (
        <li key={actualMoveIndex}>
          <button 
            onClick={() => jumpTo(actualMoveIndex)}
            className={actualMoveIndex === currentMove ? 'current-move' : ''}
          >
            {description}
          </button>
        </li>
      );
    });
  }

  function getWinningLine() {
    const win = calculateWinner(currentSquares);
    if (!win) return null;

    const winPatterns = {
      0: [0, 1, 2], // Á¨¨1Ë°å
      1: [3, 4, 5], // Á¨¨2Ë°å
      2: [6, 7, 8], // Á¨¨3Ë°å
